import React from "react";
import "./MainContainer.css";
import TaskBox from "../reusables/cards/TaskBox";
import {
  pendingTasks,
  inProgressTasks,
  completedTasks,
  deployedTasks,
  deferredTasks,
} from "../store/data";
import Button from "../reusables/buttons/Button";

const MainContainer = () => {
  return (
    <div className="main-container">
      <div className="filter-form">
        <div>
          <p>Filter By:</p>
          <form method="post" action="/">
            <input placeholder="Assignee Name"></input>
            <select name="priority" id="priority" placeholder="Priority">
              <option value="P0">P0</option>
              <option value="P1">P1</option>
              <option value="P2">P2</option>
            </select>
            <input type="date" placeholder="DD/MM/YY - DD/MM/YY"></input>
          </form>
        </div>
        <div>
          <Button name="Add New Task" />
        </div>
      </div>
      <div className="sort-form"></div>
      <div className="task-box-area">
        <TaskBox heading="Pending" bgcolor="#ABA8A2" tasks={pendingTasks} />
        <TaskBox
          heading="In Progress"
          bgcolor="#F2B32B"
          tasks={inProgressTasks}
        />
        <TaskBox heading="Completed" bgcolor="#65CD12" tasks={completedTasks} />
        <TaskBox heading="Deployed" bgcolor="#102C75" tasks={deployedTasks} />
        <TaskBox heading="Deferred" bgcolor="#f6a192" tasks={deferredTasks} />
      </div>
    </div>
  );
};

export default MainContainer;
