import React from "react";
import "./TaskCard.css";
import { HiDotsVertical } from "react-icons/hi";
import Button from "../buttons/Button";

const TaskCard = (props) => {
  return (
    <div className="task-card">
      <div className="task-card-header">
        <div>
          <h2 className="task-heading">{props.taskTitle}</h2>
        </div>
        <div className="task-priority">
          <p className="task-priority-box">{props.taskPriority}</p>
        </div>
      </div>
      <hr></hr>
      <div className="task-card-body">
        <p className="task-content">{props.taskContent}</p>
      </div>
      <div className="task-card-footer">
        <div>
          <p className="task-card-assignee">@Pravin</p>
        </div>
        <div>
          <p className="task-card-menu">
            <HiDotsVertical />
          </p>
        </div>
      </div>
      <Button name="Status" />
    </div>
  );
};

export default TaskCard;
