import React from "react";
import "./MainContainer.css";
import TaskBox from "../reusables/cards/TaskBox";

const MainContainer = () => {
  const pendingTasks = [
    {
      title: "Task 1",
      priority: "P0",
      content: "Content 1",
      assignee: "Pravin",
      status: "Assign",
    },
    {
      title: "Task 2",
      priority: "P1",
      content: "Content 2",
      assignee: "John",
      status: "Assign",
    },
    {
      title: "Task 3",
      priority: "P1",
      content: "Content 2",
      assignee: "John",
      status: "Assign",
    },
  ];

  const inProgressTasks = [
    {
      title: "Task 3",
      priority: "P2",
      content: "Content 3",
      assignee: "Alice",
      status: "In Progress",
    },
  ];

  const completedTasks = [
    {
      title: "Task 4",
      priority: "P3",
      content: "Content 4",
      assignee: "Bob",
      status: "Completed",
    },
  ];

  const deployedTasks = [
    {
      title: "Task 5",
      priority: "P0",
      content: "Content 5",
      assignee: "Charlie",
      status: "Deployed",
    },
  ];

  const deferredTasks = [
    {
      title: "Task 6",
      priority: "P1",
      content: "Content 6",
      assignee: "David",
      status: "Deferred",
    },
  ];

  return (
    <div className="main-container">
      <div></div>
      <div className="task-box-area">
        <TaskBox heading="Pending" bgcolor="#ABA8A2" tasks={pendingTasks} />
        <TaskBox
          heading="In Progress"
          bgcolor="#F2B32B"
          tasks={inProgressTasks}
        />
        <TaskBox heading="Completed" bgcolor="#65CD12" tasks={completedTasks} />
        <TaskBox heading="Deployed" bgcolor="#102C75" tasks={deployedTasks} />
        <TaskBox heading="Deferred" bgcolor="#f6a192" tasks={deferredTasks} />
      </div>
    </div>
  );
};

export default MainContainer;
